import React from 'react'
import './Expenses.css';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';

const Expenses = ({items}) => {
    return (
        <Card className="expenses">
            <ExpensesFilter>
            {items.map(item => (
                <ExpenseItem
                key={item.id}
                title={item.title}
                amount={item.amount}
                date={item.date}
                />
            ))}
           </ExpensesFilter>
        </Card>
    )
}

export default Expenses
